import React, { Component } from "react";
//import IndexInfo from "./indexInfo";
import Card from "react-bootstrap/Card";

const API =
  "https://npdpl9r340.execute-api.us-east-1.amazonaws.com/prod/marketdata/v1/index/HDAI/last";

class indexDetails extends Component {
  constructor(props) {
    super(props);

    this.state = {
      index: [],
      constituents: [],
      isLoading: false
    };
  }

  componentDidMount() {
    this.setState({ isLoading: true });

    fetch(API)
      .then(response => response.json())
      .then(data =>
        this.setState({
          index: data,
          constituents: data.constituents,
          isLoading: false
        })
      );
  }

  //  state = {};
  render() {
    const { constituents, index, isLoading } = this.state;

    if (isLoading) {
      return <p>Loading... </p>;
    } else {
      return (
        <React.Fragment>
          <Card bg="light" style={{ width: "18rem" }}>
            <Card.Body>
              <Card.Title>Current Level</Card.Title>
              <Card.Text>{index.currentValue}</Card.Text>
            </Card.Body>
          </Card>

          {index.returns.map(returns => (
            <li key={returns}>{returns}</li>
          ))}
        </React.Fragment>
      );
    }
  }
}

export default indexDetails;
