import React, { Component } from "react";
import Modal from "react-bootstrap/Modal";
import ButtonToolbar from "react-bootstrap/ButtonToolbar";
import Button from "react-bootstrap/Button";

class PopUp extends Component {
  constructor(props, context) {
    super(props, context);

    this.state = {
      lgShow: false,
      details: [],
      isLoading: false
    };
  }

  componentDidMount() {
    this.setState({ isLoading: true });

    fetch(
      "https://npdpl9r340.execute-api.us-east-1.amazonaws.com/prod/marketdata/v1/summary/BTC"
    )
      .then(response => response.json())
      .then(data => {
        console.log(data);
        this.setState({
          details: data,
          isLoading: false
        });
      });
  }

  render() {
    let lgClose = () => this.setState({ lgShow: false });
    const { details, isLoading } = this.state;

    return (
      <ButtonToolbar>
        <Button
          size="sm"
          variant="info"
          onClick={() => this.setState({ lgShow: true })}
        >
          Full Asset Detail
        </Button>

        <Modal
          size="lg"
          show={this.state.lgShow}
          onHide={lgClose}
          aria-labelledby="example-modal-sizes-title-lg"
        >
          <Modal.Header closeButton>
            <Modal.Title id="example-modal-sizes-title-lg" />
          </Modal.Header>
          <Modal.Body>Symbol: {details.symbol}</Modal.Body>
        </Modal>
      </ButtonToolbar>
    );
  }
}

export default PopUp;

// Icon| Name: {this.props.name}
// Symbol: {details.symbol}
// Market Cap: {this.props.lastPrice * details.circulating_supply}
// Last Price: {this.props.lastPrice}
// 24h Variations: {this.props.h24Var}
