import React, { Component } from "react";
import IndexInfo from "./indexInfo";
//import IndexInfo from "./indexInfo";

const API =
  "https://npdpl9r340.execute-api.us-east-1.amazonaws.com/prod/marketdata/v1/index/HDAI/last";

class indexDetails extends Component {
  constructor(props) {
    super(props);

    this.state = {
      index: [],
      constituents: [],
      isLoading: false
    };
  }

  componentDidMount() {
    this.setState({ isLoading: true });

    fetch(API)
      .then(response => response.json())
      .then(data =>
        this.setState({
          index: data,
          constituents: data.constituents,
          isLoading: false
        })
      );
  }

  //  state = {};
  render() {
    const { constituents, index, isLoading } = this.state;

    if (isLoading) {
      return <p>Loading... </p>;
    } else {
      return (
        <React.Fragment>
          <IndexInfo />
          <ul>
            {index.map(index => (
              <li key={index}>{index}</li>
            ))}
          </ul>
          <ul>
            {constituents.map(constituents => (
              <li key={constituents.assetId}>
                Name: {constituents.assetId} | YtD:{constituents.returns.YtD}
              </li>
            ))}
          </ul>
        </React.Fragment>
      );
    }
  }
}

export default indexDetails;
