import React, { Component } from "react";
import Modal from "react-bootstrap/Modal";
import ButtonToolbar from "react-bootstrap/ButtonToolbar";
import Button from "react-bootstrap/Button";
import Figure from "react-bootstrap/Figure";
import ADA from "../assets/ADA.png";
import BCH from "../assets/BCH.png";
import BCHSV from "../assets/BCHSV.png";
import BTC from "../assets/BTC.png";
import DASH from "../assets/DASH.png";
import EOS from "../assets/EOS.png";
import ETC from "../assets/ETC.png";
import ETH from "../assets/ETH.png";
import LTC from "../assets/LTC.png";
import NEO from "../assets/NEO.png";
import TRX from "../assets/TRX.png";
import XLM from "../assets/XLM.png";
import XRP from "../assets/XRP.png";

class PopUp extends Component {
  constructor(props, context) {
    super(props, context);

    this.state = {
      lgShow: false,
      details: [],
      hdarp: []
    };
  }

  componentDidMount() {
    try {
      this.setState({ isLoading: true });

      fetch(
        "https://npdpl9r340.execute-api.us-east-1.amazonaws.com/prod/marketdata/v1/summary/" +
          this.props.id
      )
        .then(response => response.json())
        .then(data => {
          console.log(data);
          this.setState({
            details: data,
            hdarp: data.hdarp
          });
        });
      setInterval(async () => {
        this.setState({ isLoading: true });

        fetch(
          "https://npdpl9r340.execute-api.us-east-1.amazonaws.com/prod/marketdata/v1/summary/" +
            this.props.id
        )
          .then(response => response.json())
          .then(data => {
            console.log(data);
            this.setState({
              details: data,
              hdarp: data.hdarp
            });
          });
      }, 30000);
    } catch (e) {
      console.log(e);
    }
  }

  render() {
    let lgClose = () => this.setState({ lgShow: false });
    const { details, hdarp } = this.state;

    return (
      <ButtonToolbar>
        <Button
          size="sm"
          variant="info"
          onClick={() => this.setState({ lgShow: true })}
        >
          Full Asset Detail
        </Button>

        <Modal
          size="lg"
          show={this.state.lgShow}
          onHide={lgClose}
          aria-labelledby="example-modal-sizes-title-lg"
        >
          <Modal.Header closeButton>
            <Modal.Title id="example-modal-sizes-title-lg">
              {this.props.name}
            </Modal.Title>
          </Modal.Header>
          <Modal.Body>
            Icon:{" "}
            <Figure>
              <Figure.Image
                width={180}
                height={180}
                alt="Asset Logo"
                src={ETH}
              />
            </Figure>
            Name: {this.props.name}
            Symbol: {details.symbol}
            Market Cap: {this.props.lastPrice * details.circulating_supply}
            Last Price: {this.props.lastPrice}
            24h Variations: {this.props.h24Var}
            Open: {hdarp.open}
            High: {hdarp.high}
            Low: {hdarp.low}
          </Modal.Body>
        </Modal>
      </ButtonToolbar>
    );
  }
}

export default PopUp;
