import React, { Component } from "react";
import Chart from "./chart";

const API =
  "https://npdpl9r340.execute-api.us-east-1.amazonaws.com/prod/marketdata/v1/index/HDAI/history?from=2019-01-01&to=2050-01-01&groupBy=1d";

class HistoricalChart extends Component {
  constructor() {
    super();

    this.state = {
      data: [],
      labels: [],
      chartData: {},
      isLoading: false
    };
  }

  componentDidMount() {
    this.setState({ isLoading: true });

    fetch(API)
      .then(response => response.json())
      .then(data =>
        this.setState({
          data: data.currentValue,
          labels: data.time,
          isLoading: false //WTF?????? only works when its false..
        })
      );
  }

  componentWillMount() {
    this.getChartData();
  }

  getChartData() {
    //ajax calls here
    this.setState({
      chartData: {
        labels: this.state.labels,
        datasets: [
          {
            label: "Historical Chart",
            data: this.state.data,
            backgroundColor: ["rgba(75, 192, 192, 0.6)"]
          }
        ]
      }
    });
  }

  render() {
    const { chartData, isLoading } = this.state;

    if (isLoading) {
      return <p>Loading... </p>;
    } else {
      return (
        <React.Fragment>
          <ul>
            {data.map(data => (
              <li key={data}>{data}</li>
            ))}
          </ul>
        </React.Fragment>
      );
    }
  }
}

export default HistoricalChart;
