import React, { Component } from "react";
import Modal from "react-bootstrap/Modal";
import ButtonToolbar from "react-bootstrap/ButtonToolbar";
import Button from "react-bootstrap/Button";
import Multiply from "./multiply";

const API =
  "https://npdpl9r340.execute-api.us-east-1.amazonaws.com/prod/marketdata/v1/summary/​" +
  `${this.props.id}`;

class PopUp extends Component {
  constructor(props, context) {
    super(props, context);

    this.state = {
      lgShow: false,
      details: [],
      isLoading: false
    };
  }

  componentDidMount() {
    this.setState({ isLoading: true });

    fetch(API)
      .then(response => response.json())
      .then(data =>
        this.setState({
          details: data,
          isLoading: false
        })
      );
  }

  render() {
    let lgClose = () => this.setState({ lgShow: false });
    const { details, isLoading } = this.state;

    return (
      <ButtonToolbar>
        <Button
          size="sm"
          variant="info"
          onClick={() => this.setState({ lgShow: true })}
        >
          Full Asset Detail
        </Button>

        <Modal
          size="lg"
          show={this.state.lgShow}
          onHide={lgClose}
          aria-labelledby="example-modal-sizes-title-lg"
        >
          <Modal.Header closeButton>
            <Modal.Title id="example-modal-sizes-title-lg">
              {" "}
              {this.props.name} | {this.props.id}{" "}
            </Modal.Title>
          </Modal.Header>
          <Modal.Body>
            Icon Name: {this.props.name}
            ID: {this.props.id}
            Market Cap:
            {
              <Multiply
                one={this.props.lastPrice}
                two={details.circulating_supply}
              />
            }
            Last Price: {this.props.lastPrice}
            24h Variation: {this.props.h24Var}
            Open: {details.coincap.open}
            High: {details.coincap.high}
            Low: {details.coincap.low}
          </Modal.Body>
        </Modal>
      </ButtonToolbar>
    );
  }
}

export default PopUp;
