import React, { Component } from "react";
//import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.css";
import NavigationBar from "./components/navigationBar";
import IndexDetails from "./components/indexDetails";
import Footer from "./components/footer";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      currentTime: new Date()
    };

    setInterval(() => {
      this.setState({
        currentTime: new Date()
      });
    }, 30000); //this is 3 seconds
  }
  render() {
    return (
      <React.Fragment>
        <h1 className="ui header">
          <div className="sub header"> Time </div>
          {this.state.currentTime.toLocaleTimeString()}
        </h1>

        <NavigationBar time={this.state.currentTime.toLocaleTimeString()} />
        <IndexDetails />
        <Footer time={this.state.currentTime.toLocaleTimeString()} />
      </React.Fragment>
    );
  }
}

export default App;
