import React, { Component } from "react";
import Card from "react-bootstrap/Card";
import Modal from "react-bootstrap/Modal";
import ButtonToolbar from "react-bootstrap/ButtonToolbar";
import Button from "react-bootstrap/Button";

class AssetInfo extends Component {
  constructor(props, context) {
    super(props, context);

    this.state = {
      lgShow: false
    };
  }

  render() {
    let lgClose = () => this.setState({ lgShow: false });

    return (
      <React.Fragment>
        <ButtonToolbar>
          <Button onClick={() => this.setState({ lgShow: true })}>
            Large modal
          </Button>

          <Modal
            size="lg"
            show={this.state.lgShow}
            onHide={lgClose}
            aria-labelledby="example-modal-sizes-title-lg"
          >
            <Modal.Header closeButton>
              <Modal.Title id="example-modal-sizes-title-lg">
                Large Modal
              </Modal.Title>
            </Modal.Header>
            <Modal.Body>...</Modal.Body>
          </Modal>
        </ButtonToolbar>

        <Card style={{ width: "18rem" }}>
          <Card.Body>
            <Card.Title> {this.props.name} </Card.Title>
            <Card.Subtitle className="mb-2 text-muted">
              {this.props.id}
            </Card.Subtitle>
            <Card.Text>
              Last Price: {this.props.lastPrice}
              24 Hour Variation: {this.props.h24Var}
              Current Market Cap: {this.props.marketCap}
              Index Weight: {this.props.weight}
            </Card.Text>
            <Card.Link href="#">Click for Full Details</Card.Link>
          </Card.Body>
        </Card>
      </React.Fragment>
    );
  }
}

export default AssetInfo;
