import React, { Component } from "react";
import IndexInfo from "./indexInfo";
import HistoricalChart from "./historicalChart";
import AssetInfo from "./assetInfo";

const API =
  "https://npdpl9r340.execute-api.us-east-1.amazonaws.com/prod/marketdata/v1/index/HDAI/last";

class IndexDetails extends Component {
  constructor(props) {
    super(props);

    this.state = {
      index: [],
      returns: [],
      constituents: [],
      isLoading: false
    };
  }

  componentDidMount() {
    this.setState({ isLoading: true });

    fetch(API)
      .then(response => response.json())
      .then(data =>
        this.setState({
          index: data,
          returns: data.returns,
          constituents: data.constituents,
          isLoading: false //WTF WHY DOES IT WORK WHEN ITS FALSE
        })
      );
  }

  render() {
    const { index, returns, isLoading } = this.state;

    if (isLoading) {
      return <p>Loading... </p>;
    } else {
      return (
        <React.Fragment>
          <IndexInfo />
          <HistoricalChart />
          <AssetInfo />
        </React.Fragment>
      );
    }
  }
}

export default IndexDetails;
